import { createStore } from "vuex";

export default createStore({
  state: () => ({
    //data
    revele: false,
    item: {},
    Clients: [],
    User: [],
    Devis: [],
    EditDevis: {},
    link: {},
  }),
  mutations: {},
  actions: {
    toggleModal({ state }, payload) {
      console.log(payload.event);

      if (payload.data) {
        state.item = { ...payload.data };
      }

      if (payload.status && !payload.event.closest(".dropdown"))
        return (state.revele = payload.status);

      if (payload.target) {
        if (
          !payload.target.closest(".modale") ||
          payload.target.id == "close-btn"
        )
          state.revele = false;
      } else state.revele = false;
    },

    async getClients({ state }) {
      let res = await fetch(`${process.env.VUE_APP_ROOT_API}/clients`);
      let data = await res.json();
      console.log(data);
      state.Clients = data[0].clients;
    },
    //   state.Clients = data[0].clients;

    async getUser({ state }) {
      let res = await fetch(`${process.env.VUE_APP_ROOT_API}/users`);
      let data = await res.json();
      console.log(data);
      state.User = data;
    },

    async getDevis({ state }, payload) {
      let res = await fetch(`${process.env.VUE_APP_ROOT_API}/devis`);
      let data = await res.json();
      state.Devis = data.reverse();
      if (payload) {
        state.EditDevis = { ...data.find((el) => el.uuid == payload.uuid) };
        console.log(payload.uuid);
      }
    },

    Download(payload) {
      if (payload) {
        window.location = `https://hkos-back.herokuapp.com/devis/download/${payload}`;
      }
    },
  },
});
